import React, { useState } from "react";
import { Link } from 'react-router-dom';
import logo from '../img/oina.png';




function Singup() {
    const [Email, setEmail] = useState('')
    const [Password, setPassword] = useState('')
    const [FirstName, setFirstName] = useState('')
    const [LastName, setLastName] = useState('')
    const [Phone, setPhone] = useState('')

    async function registerUser(event) {
        event.preventDefault()

        const userData = {
            Email,
            Phone,
            Password,
            FirstName,
            LastName
        };
        const response = await fetch('http://localhost:8085/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
        });

        const data = await response.json()

        if (data.token) {
            localStorage.setItem('token', data.token)
            alert('Registration successful')
            window.location.href = '/Home'
        } else {
            alert('Something wrong')
        }

    }


    return (
        <div className="Singup">
            <img src={logo} alt="" />
            <form onSubmit={registerUser}>
                <div className="form-text">
                    <h4>Имя</h4>
                    <input type="text" name="FirstName" id="FirstName" placeholder="Введите имя" minlength="3" accept="*/*" value={FirstName} onChange={(e) => setFirstName(e.target.value)} />
                </div>
                <div className="form-text">
                    <h4>Фамилия</h4>
                    <input type="text" name="LastName" id="LastName" placeholder="Введите фамилию" minlength="4" accept="*/*" value={LastName} onChange={(e) => setLastName(e.target.value)} />
                </div>
                <div className="form-text">
                    <h4>Номер телефона</h4>
                    <input type="text" name="Phone" id="Phone" placeholder="Введите номер телефона" minlength="12" accept="*/*" value={Phone} onChange={(e) => setPhone(e.target.value)} />
                </div>
                <div className="form-text">
                    <h4>Электронная почта</h4>
                    <input type="text" name="Email" id="Email" placeholder="Введите электронную почту" minlength="13" accept="*/*" value={Email} onChange={(e) => setEmail(e.target.value)} />
                </div>
                <div className="form-text">
                    <h4>Пароль</h4>
                    <input type="password" name="Password" id="Password" placeholder="Введите пароль" minlength="8" accept="*/*" value={Password} onChange={(e) => setPassword(e.target.value)} />
                </div>
                <br /><button type="submit" className="button" >Зарегистрироваться</button>
                <br />Уже создан аккаунт? <Link to="/Singin">Войдите!</Link>
            </form>
        </div>
    )


}

export default Singup;
