import React, { useEffect, useState } from "react";
import { Routes, Route, Link, useNavigate } from 'react-router-dom';
import logo from '../img/oina.png';

function Singup() {
    let navigate = useNavigate();

    const [Email, setEmail] = useState('')
    const [Password, setPassword] = useState('')
    const [FirstName, setFirstName] = useState('')
    const [LastName, setLastName] = useState('')
    const [Phone, setPhone] = useState('')

    async function registerUser(event) {
        event.preventDefault()

        const response = await fetch('http://localhost:8085/register', {
            method: 'POST',
            headers: {
                'Content Type' : 'application/json',
            },
            body: JSON.stringify({
                LastName,
                FirstName,
                Phone,
                Email,
                Password,
            }), 
        })

        const data = await response.json()

        if (data.status === 'ok') {
            navigate('/Singin');
        }
    }

    return (
        <div className="Singup">
            <img src={logo} alt="" />
            <form action="">
                <div className="form-text">
                    <h4>Имя</h4>
                    <input type="text" name="first name" id=" first name" placeholder="Введите имя" onChange={(e) => setFirstName(e.target.value)}/>
                </div>
                <div className="form-text">
                    <h4>Фамилия</h4>
                    <input type="text" name="last name" id="last name" placeholder="Введите фамилию" onChange={(e) => setLastName(e.target.value)}/>
                </div>
                <div className="form-text">
                    <h4>Номер телефона</h4>
                    <input type="text" name="phone number" id="phone number" placeholder="Введите номер телефона" onChange={(e) => setPhone(e.target.value)}/>
                </div>
                <div className="form-text">
                    <h4>Электронная почта</h4>
                    <input type="login" name="login" id="login" placeholder="Введите электронную почту" onChange={(e) => setEmail(e.target.value)}/>
                </div>
                <div className="form-text">
                    <h4>Пароль</h4>
                    <input type="password" name="password" id="password" placeholder="Введите пароль" onChange={(e) => setPassword(e.target.value)}/>
                </div>
                <br /><button type="submit" className="button" >Зарегистрироваться</button>
                <br />Уже создан аккаунт? <Link to="/Singin">Войдите!</Link>
            </form>
        </div>
    )
}

export default Singup;
